#!/usr/bin/env python3

# https://leetcode.com/problems/3sum

class Solution:
    def threeSum(self, nums: list[int]) -> list[list[int]]:
        n = sorted(nums)
        ans = []

        for i in range(len(n)):
            if n[i] == n[i-1] == n[i-2] == 0: ans.append((0, 0, 0))
            if n[i] == n[i-1]: continue
            sp = i+1
            ep = len(n)-1

            while sp < ep:
                if n[sp] + n[ep] + n[i] > 0:
                    ep -= 1
                elif n[sp] + n[ep] + n[i] < 0:
                    sp += 1
                else:
                    ans.append((n[i], n[sp], n[ep]))
                    ep -= 1
                    #  print(f"i: {i}\tsp: {sp}\tep: {ep}")

        return [list(c) for c in set(ans)]

